sysauto_script.script:
//These skills are disabled by default, activate these skills so they don't have to be manually enabled.

try {
    var activateSkillsGR = new GlideRecord("sn_nowassist_skill_config");
    activateSkillsGR.query();
    while (activateSkillsGR.next()) {
        activateSkillsGR.setValue("active", true);
        activateSkillsGR.setValue("in_product_active", true);
        activateSkillsGR.update();
    }
    var activateNPGR = new GlideRecord("sys_gen_ai_skill_applicability");
    activateNPGR.query();
    while (activateNPGR.next()) {
        activateNPGR.setValue("active", true);
        activateNPGR.update();
    }
} catch (error) {
    gs.error("Gen AI Baseline for Creator - Failed while activating Now Assist Skills  - Error - " + error.message);
}
